import React, { useContext, useState } from "react";
import Week from "./MonthCalendarWeek";
import styled from "@emotion/styled";
import { EventContexts } from "./EventContexts";
import SimpleMenu from "./SimpleMenu";


const Table = styled.table`
  border: 1px solid black;
  width: 80%;
  min-width: 450px;
  position: fixed;
  left: 50%;
  transform: translateX(-50%);
  margin-top: 0;
  table-layout: fixed;
  background: white;
`;
const Thead = styled.thead`
  margin-bottom: 0;
  padding-bottom: 0;
`;

const weekIds = [1, 2, 3, 4, 5];
const months = [
  "January",
  "February",
  "March",
  "April",
  "May",
  "June",
  "July",
  "August",
  "September",
  "October",
  "November",
  "December",
];

const Button = styled.button`
  border-top: 1px solid #3da850;
  background: #aac2ae;
  background: -webkit-gradient(linear, left top, left bottom, from(#48b55c), to(#aac2ae));
  background: -webkit-linear-gradient(top, #48b55c, #aac2ae);
  background: -moz-linear-gradient(top, #48b55c, #aac2ae);
  background: -ms-linear-gradient(top, #48b55c, #aac2ae);
  background: -o-linear-gradient(top, #48b55c, #aac2ae);
  padding: 5px 10px;
  -webkit-border-radius: 4px;
  -moz-border-radius: 4px;
  border-radius: 4px;
  -webkit-box-shadow: rgba(0,0,0,1) 0 1px 0;
  -moz-box-shadow: rgba(0,0,0,1) 0 1px 0;
  box-shadow: rgba(0,0,0,1) 0 1px 0;
  text-shadow: rgba(0,0,0,.4) 0 1px 0;
  color: white;
  font-size: 13px;
  font-family: Georgia, Serif;
  text-decoration: none;
  vertical-align: middle;
  & hover {
    border-top-color: #28a669;
    background: #28a669;
    color: #ccc;
  }
  & active {
    border-top-color: #1b5c34;
    background: #1b5c34;
  }
`;

const MonthCalendar = React.memo(() => {
  const { month, year } = useContext(EventContexts);


  return (
    <div>
      <Table>
        <Thead>
          <tr>
            <td>{months[month]}</td>
            <td>
              <Button>Prev Month</Button>
            </td>
            <td><Button>Next Month</Button></td>
          </tr>
        </Thead>
        <tbody>
          {weekIds.map((id) => (
            <Week year={year} month={month} key={"week" + id} id={id} />
          ))}
        </tbody>
      </Table>
    </div>
  );
});

export default MonthCalendar;
